mapper.select:
select b.seq , b.title , b.content , b.id , if(DATE_FORMAT(now(), '%c-%d') = DATE_FORMAT(b.registDate, '%c-%d'),DATE_FORMAT(b.registDate, '%H:%i'), DATE_FORMAT(b.registDate, '%m/%d')) as registDate , DATE_FORMAT(modifyDate, '%c-%d %h:%i') as modifyDate , deleteYn , b.viewCount , c.commentCount , case when b.noticeYn = 'N' then row_number() over (order by b.noticeYn asc, b.seq desc) end as rowNum , b.noticeYn from board b left join (select count(*) as commentCount, boardSeq from comment group by boardSeq) c on b.seq = c.boardSeq where deleteYn = 'N' and category = #{category}
and b.title like "%${content}%" || b.content like "%${content}%"
and b.title like "%${title}%"
and b.content like "%${content}%"
and b.id like "%${id}%"
order by b.noticeYn desc, b.seq desc limit #{pagePerNum} offset #{offset}

mapper.update:
update board
set viewCount = viewCount + 1
where seq = #{seq}

mapper.insert:
insert into board (
		title,content,id,registDate,modifyDate,deleteYn, noticeYn, category
	) 
	values (
		#{title},#{content},#{id},now(),now(),'N',#{noticeYn}, #{category}
	)

mapper.update:
    	update board
		set
    		title = #{title}
    		,content = #{content}
    		,modifyDate = now()
    	where seq = #{seq}

mapper.insert:
insert into comment (
	content, boardSeq, id, registDate
)
values (
	#{content}, #{boardSeq}, #{id}, now()
)

insert into comment (
content, boardSeq, id, registDate, followSeq
)
values (
#{content}, #{boardSeq}, #{id}, now(), #{followSeq}
)

insert into member (
id, gameId, password, role
)
values (
#{id}, #{gameId}, #{password}, 'USER'
)

mapper.update:
update member
	set role = #{role}
where id = #{id}
